function setHoverEffect(element, trigger) {
    trigger.addEventListener("mouseenter", () => {
        gsap.to(element, {
            width: '100%',
            duration: 2
        });
    });

    trigger.addEventListener("mouseleave", () => {
        gsap.to(element, {
            width: '0%',
            duration: 2
        });
    });
}

